import React, { useState, useEffect } from 'react';
import { Search, X } from 'lucide-react';
import citiesData from './citiesData.json';
import './styles/WriteTravelGuide.css';

const WriteTravelGuide = () => {
  const [searchTerm, setSearchTerm] = useState('');
  const [suggestions, setSuggestions] = useState([]);
  const [selectedCities, setSelectedCities] = useState([]);

  useEffect(() => {
    if (searchTerm.length > 1) {
      const filteredCities = citiesData.cities.filter(city =>
        city.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
        city.country.toLowerCase().includes(searchTerm.toLowerCase())
      ).slice(0, 5);
      setSuggestions(filteredCities);
    } else {
      setSuggestions([]);
    }
  }, [searchTerm]);

  const handleAddCity = (city) => {
    if (selectedCities.length < 10 && !selectedCities.some(c => c.name === city.name)) {
      setSelectedCities([...selectedCities, city]);
      setSearchTerm('');
      setSuggestions([]);
    }
  };

  const handleRemoveCity = (cityToRemove) => {
    setSelectedCities(selectedCities.filter(city => city.name !== cityToRemove.name));
  };

  return (
    <div className="write-travel-guide">
      <nav className="navigation">
        <img src="/api/placeholder/120/40" alt="Wanderlog logo" className="logo" />
        <div className="nav-links">
          <a href="#" className="nav-link">Home</a>
          <a href="#" className="nav-link">Travel guides</a>
          <a href="#" className="nav-link">Hotels</a>
          <div className="search-container">
            <input type="text" placeholder="Explore by destination" className="nav-search-input" />
            <Search className="nav-search-icon" />
          </div>
          <button className="login-button">Log in</button>
          <button className="signup-button">Sign up</button>
        </div>
      </nav>

      <h1 className="main-title">Write a travel guide</h1>
      <p className="subtitle">Help fellow travelers by writing up your tips or a past itinerary.</p>

      <div className="search-section">
        <input
          type="text"
          placeholder="For where? e.g. Paris, Hawaii, Japan"
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
          className="city-search-input"
        />
        {suggestions.length > 0 && (
          <ul className="suggestions-list">
            {suggestions.map((city, index) => (
              <li key={index} onClick={() => handleAddCity(city)} className="suggestion-item">
                {city.name}, {city.country}
              </li>
            ))}
          </ul>
        )}
      </div>

      <div className="selected-cities">
        {selectedCities.map((city, index) => (
          <div key={index} className="city-tag">
            <span>{city.name}</span>
            <X className="remove-city-icon" onClick={() => handleRemoveCity(city)} />
          </div>
        ))}
      </div>

      <div className="action-section">
        <button className="start-writing-button">Start writing</button>
        <p className="alternative-action">Or start planning a trip</p>
      </div>
    </div>
  );
};

export default WriteTravelGuide;
