import React, { useState, useEffect } from 'react';
import { Search, X, Bell, User, Calendar, Plus, ChevronDown, Users, Globe, Lock } from 'lucide-react';
import { Link, useNavigate } from 'react-router-dom';
import citiesData from '../Data/CitiesData.json';
import '../styles/WriteTravelGuide.css';
import '../styles/HomePage.css'

const WriteTravelGuide = () => {
  const [searchTerm, setSearchTerm] = useState('');
  const [suggestions, setSuggestions] = useState([]);
  const [selectedCities, setSelectedCities] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    if (searchTerm.length > 1) {
      const filteredCities = citiesData.cities.filter(city =>
        city.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
        city.country.toLowerCase().includes(searchTerm.toLowerCase())
      ).slice(0, 5);
      setSuggestions(filteredCities);
    } else {
      setSuggestions([]);
    }
  }, [searchTerm]);

  const handleAddCity = (city) => {
    if (selectedCities.length < 10 && !selectedCities.some(c => c.name === city.name)) {
      setSelectedCities([...selectedCities, city]);
      setSearchTerm('');
      setSuggestions([]);
    }
  };

  const handleRemoveCity = (cityToRemove) => {
    setSelectedCities(selectedCities.filter(city => city.name !== cityToRemove.name));
  };

  return (
    <div className="tinoto-write-guide">
      <nav className="tinoto-nav">
        <div className="tinoto-nav-left">
          <Link to="/" className="tinoto-logo">
            <img src="/api/placeholder/120/40" alt="Tinoto logo" />
          </Link>
          <div className="tinoto-nav-links">
            <Link to="/" className="tinoto-nav-link">Home</Link>
            <Link to="/travel-guides" className="tinoto-nav-link">Travel guides</Link>
            <Link to="/hotels" className="tinoto-nav-link">Hotels</Link>
          </div>
        </div>
        <div className="tinoto-nav-right">
          <div className="tinoto-search-container">
            <input type="text" placeholder="Explore by destination" className="tinoto-search-input" />
            <Search className="tinoto-search-icon" />
          </div>
          <button className="tinoto-login-button">Log in</button>
          <button className="tinoto-signup-button">Sign up</button>
        </div>
      </nav>

      <main className="tinoto-main">
        <h1 className="tinoto-main-title">Write a travel guide</h1>
        <p className="tinoto-subtitle">Help fellow travelers by writing up your tips or a past itinerary.</p>

        <div className="tinoto-search-section">
          <input
            type="text"
            placeholder="For where? e.g. Paris, Hawaii, Japan"
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
            className="tinoto-city-search-input"
          />
          {suggestions.length > 0 && (
            <ul className="tinoto-suggestions-list">
              {suggestions.map((city, index) => (
                <li key={index} onClick={() => handleAddCity(city)} className="tinoto-suggestion-item">
                  {city.name}, {city.country}
                </li>
              ))}
            </ul>
          )}
        </div>

        <div className="tinoto-selected-cities">
          {selectedCities.map((city, index) => (
            <div key={index} className="tinoto-city-tag">
              <span>{city.name}</span>
              <X className="tinoto-remove-city-icon" onClick={() => handleRemoveCity(city)} />
            </div>
          ))}
        </div>

        <div className="tinoto-action-section">
          <button className="tinoto-start-writing-button">Start writing</button>
          <p className="tinoto-alternative-action">
            Or <Link to="/home" className="tinoto-link">start planning a trip</Link>
          </p>
        </div>
      </main>
    </div>
  );
};

export default WriteTravelGuide;
